import React from 'react'
import { Link } from 'react-router-dom'
import Rating from '../components/Rating'
import products from '../products'

function ProductScreen({ match }) {
  const { id } = match.params; // Destructure the id from match.params
  const product = products.find((p) => p._id === id); // Use strict equality (===) to compare ids
  
  if (!product) {
    return <div>Product not found</div>; // Handle case when product is not found
  }

  return (
    <div>
      <h2>{product.name}</h2>
      <Rating rating={product.rating} numReviews={product.numReviews} />
      <p>Price: ${product.price}</p>
      <p>Description: {product.description}</p>
      <Link to="/products">Back to Products</Link>
    </div>
  );
}

export default ProductScreen
