import React from 'react'
import { Link } from 'react-router-dom'
import Rating from '../components/Rating'
import products from '../products'

function ProductScreen({ match }) {
  // Initialize product as null
  let product = null;

  // Loop through the products array to find the matching product
  for (let i = 0; i < products.length; i++) {
    if (products[i]._id === match.params.id) {
      product = products[i];
      break; // Exit the loop once a matching product is found
    }
  }

  // Check if a product was found
  if (!product) {
    return <div>Product not found</div>; // Handle the case where no product is found
  }

  // Render the product information
  return (
    <div>
      {product.name}
    </div>
  );
}


export default ProductScreen
